) VALUES (
<#list attr_list as attr> <#if attr?ends_with("_")> <#-- 变量赋值 --> <#assign newAttr=attr?substring(0,attr?length-1)/> #${newAttr}# <#elseif attr?ends_with("_d")> <#-- 变量赋值 --> <#assign newAttr1=attr?substring(0,attr?length-2)/> #${newAttr1}# <#else> #${attr}# </#if> </#list>
